<head></head>

<body>
  <app-home></app-home>

  <h2>Reservation List</h2>
  <table>
    <thead>
      <tr>
        <th>G</th>
      </tr>
    </thead>
    <tbody></tbody>
  </table>

  <ul *ngIf="reservations.length; else noReservation">
    <li *ngFor="let reservation of reservations">
      Booking ID : {{ reservation.bookingId }} <br />
      Guest Name : {{ reservation.guestName }} <br />
      Guest Email : {{ reservation.guestEmail }} <br />
      Check In Date : {{ reservation.checkInDate }} <br />
      Check Out Date : {{ reservation.checkOutDate }} <br />
      Room Number : {{ reservation.roomNumber }} <br />
      <button (click)="deleteReservation(reservation.bookingId)">Delete</button>
      <button [routerLink]="['/edit', reservation.bookingId]">Edit</button>
    </li>
    <br />
    <br />
  </ul>

  <ng-template #noReservation> No Reservation Available</ng-template>
</body>
