import { Component } from '@angular/core';
import { ReservationService } from '../reservation/reservation.service';
import { ReservationModule } from '../reservation/reservation.module';
import { OnInit } from '@angular/core';
import { Reservation } from '../model/reservation';

@Component({
  selector: 'app-reservation-list',
  templateUrl: './reservation-list.component.html',
  styleUrls: ['./reservation-list.component.css'],
})
export class ReservationListComponent implements OnInit {
  reservations: Reservation[] = [];

  constructor(private reservationService: ReservationService) {}

  ngOnInit(): void {
    this.reservations = this.reservationService.getReservations();
  }

  deleteReservation(id: string) {
    this.reservationService.deleteReservation(id);
  }

  updateRservation(reservation : Reservation){
    this
  }
}
<!-- <p>reservation-list works!</p> -->

<h2>Reservation List</h2>

<ul *ngIf="reservations.length; else noReservation">
  <li *ngFor="let reservation of reservations">
    Booking ID : {{ reservation.id }} <br />
    Guest Name : {{ reservation.guestName }} <br />
    Guest Email : {{ reservation.guestId }} <br />
    Check In Date : {{ reservation.checkInDate }} <br />
    Check Out Date : {{ reservation.checkOutDate }} <br />
    Room Number : {{ reservation.roomNumber }} <br>
    <button (click)="deleteReservation(reservation.id)">Delete</button> 
    <button (click)="updateReservation">Edit</button>
  </li>
  <br />
  <br />
</ul>

<ng-template #noReservation> No Reservation Available</ng-template>
