import { Component } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {

}
<head>
  <link rel="icon" type="image/x-icon" href="hotelFavIcon.png" />
</head>
<body>
  <div>
    <h1>Welcome to the Hotel Reservation Portal !!</h1>
    <button [routerLink]="['/new']">Create a new Reservation</button>
    <button [routerLink]="['/list']">View all Reservations</button>
  </div>
</body>
